mapper.select:
SELECT ID , SOCIAL_ID , PASSWORD , NAME , SSN , NICKNAME , EMAIL , MOBILE , REG_DATE , ZIPCODE , ROAD_ADDR , DETAIL_ADDR , JIBUN_ADDR , FILE_NAME FROM MEMBER
AND ID = #{id}
AND PASSWORD = HASHBYTES('SHA2_256', CAST(#{password} as VARBINARY(MAX)))

mapper.insert:
	   INSERT INTO MEMBER (
	           ID
           ,   SOCIAL_ID
           ,   PASSWORD
           ,   NAME
           ,   SSN
           ,   NICKNAME
           ,   EMAIL
           ,   MOBILE
           ,   REG_DATE
           ,   ZIPCODE
           ,   ROAD_ADDR
           ,   DETAIL_ADDR
           ,   JIBUN_ADDR
           ,   FILE_NAME
	   ) VALUES (
	           #{id}
           ,   #{socialId}
           ,   HASHBYTES('SHA2_256', #{password})
		   ,   #{name}
		   ,   #{ssn}
		   ,   #{nickname}
		   ,   #{email}
		   ,   #{mobile}
		   ,   #{regDate}
		   ,   #{zipcode}
		   ,   #{roadAddr}
		   ,   #{detailAddr}
		   ,   #{jibunAddr}
           ,   #{fileName}
	   )

mapper.update:
        UPDATE  MEMBER
        SET     ID = #{id}
			,   SOCIALID = #{socialId}
			,   PASSWORD = HASHBYTES('SHA2_256', #{password})
			,   NAME = #{name}
			,   SSN = #{ssn}
			,   NICKNAME = #{nickname}
			,   EMAIL = #{email}
			,   MOBILE = #{mobile}
			,   REGDATE = #{regDate}
			,   ZIPCODE = #{zipcode}
			,   ROADADDR = #{roadAddr}
			,   DETAILADDR = #{detailAddr}
			,   JIBUNADDR = #{jibunAddr}
			,   FILENAME = #{filename}


                AND ID = #{id}


                AND PASSWORD = HASHBYTES('SHA2_256', #{password})

mapper.delete:
DELETE  FROM    MEMBER


        AND ID = #{id}


        AND SOCIALID = #{socialId}


        AND PASSWORD = HASHBYTES('SHA2_256', #{password})
