mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY d.NO DESC) AS rn, d.NO, d.TITLE, d.CONTENT, m.NICKNAME, d.WRITE_DATE, d.READ_COUNT, d.FILE_NAME FROM DAILYBOARD d JOIN MEMBER m ON d.ID = m.ID
AND m.NICKNAME LIKE CONCAT('%', #{keyword}, '%')
AND ${keyField} LIKE CONCAT('%', #{keyword}, '%')
) AS subquery WHERE rn BETWEEN #{startRow} AND #{endRow}

mapper.insert:
	   INSERT INTO DAILYBOARD (
                TITLE
            ,   CONTENT
            ,   ID
            ,   FILE_NAME
	   ) VALUES (
                #{title}
            ,   #{content}
            ,   #{id}
            ,   #{fileName}
	   )

mapper.update:
UPDATE  DAILYBOARD
SET     TITLE = #{title}
    ,   CONTENT = #{content}
    ,   ID = #{id}
    ,   FILE_NAME = #{fileName}
WHERE   NO = #{no}

mapper.select:
SELECT   MAX(NO)
FROM     DAILYBOARD
WHERE    ID = #{id}

mapper.update:
        UPDATE  DAILYBOARD
		SET     READ_COUNT = READ_COUNT + 1
		WHERE   NO = #{no}

mapper.insert:
INSERT INTO DAILYBOARDFILES (
        BOARD_NO
    ,   FILE_NAME
) VALUES (
        #{boardNo}
    ,   #{fileName}
)
